import React, { useState } from "react";
import * as XLSX from "xlsx";
import './GasStationReport.css'; // Nhập file CSS

const GasStationReport = () => {
  const [file, setFile] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [totalAmount, setTotalAmount] = useState(null);

  // State để kiểm tra input
  const [error, setError] = useState({ start: false, end: false });

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    setFile(file);
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        const data = event.target.result;
        const workbook = XLSX.read(data, { type: "binary" });
        const firstSheetName = workbook.SheetNames[0];
        const worksheet = workbook.Sheets[firstSheetName];
        const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

        // Chuyển đổi dữ liệu sang dạng có thể xử lý
        const formattedData = jsonData.slice(1).map((row) => ({
          time: row[0], // Thời gian giao dịch
          amount: row[3], // Thành tiền
        }));
        setTransactions(formattedData);
      };
      reader.readAsBinaryString(file);
    }
  };

  const handleCalculate = () => {
    // Kiểm tra input có rỗng không
    setError({
      start: !startTime,
      end: !endTime,
    });

    if (!startTime || !endTime) return; // Dừng nếu một trong hai input rỗng

    // Chuyển đổi thời gian input thành dạng có thể so sánh
    const start = new Date(`1970-01-01T${startTime}:00`);
    const end = new Date(`1970-01-01T${endTime}:00`);

    // Tính tổng "Thành tiền" trong khoảng thời gian
    const total = transactions.reduce((acc, transaction) => {
      const transactionTime = new Date(`1970-01-01T${transaction.time}:00`);
      if (transactionTime >= start && transactionTime <= end) {
        return acc + parseFloat(transaction.amount);
      }
      return acc;
    }, 0);

    setTotalAmount(total);
  };

  return (
    <div className="container">
      <h2>Gas Station Transaction Report</h2>
      <div>
        <input type="file" onChange={handleFileUpload} />
      </div>

      <div>
        <label>
          Start Time (24h):
          <input
            type="time"
            value={startTime}
            onChange={(e) => {
              setStartTime(e.target.value);
              setError((prev) => ({ ...prev, start: false })); // Reset lỗi khi có input
            }}
            className={error.start ? "input-error" : ""}
          />
        </label>
        <label>
          End Time (24h):
          <input
            type="time"
            value={endTime}
            onChange={(e) => {
              setEndTime(e.target.value);
              setError((prev) => ({ ...prev, end: false })); // Reset lỗi khi có input
            }}
            className={error.end ? "input-error" : ""}
          />
        </label>
      </div>

      <button onClick={handleCalculate}>Calculate Total</button>

      {totalAmount !== null && (
        <div>
          <h3>Total Amount: {totalAmount.toLocaleString()} VND</h3>
        </div>
      )}
    </div>
  );
};

export default GasStationReport;
